site.headStyle:
@import url("https://fonts.cdnfonts.com/css/codec-pro");

html {
  font-family: "Codec Pro", sans-serif;
  scroll-behavior: smooth;
}

/* Highlight */
.highlight {
  line-height: 1;
  display: inline-block;
  position: relative;
  padding: 2px;
}

.highlight::before {
  content: "";
  position: absolute;
  width: calc(100% + 4px);
  height: calc(100% + 4px);
  bottom: 0;
  left: -2px;
  background-color: #fbd116;
  border-radius: 5px;
  transition: transform 0.5s ease-out;
  z-index: -1;
  transform: scaleX(0);
}

.highlight.animate::before {
  transform: scaleX(1);
}

/* Buttons */

.button {
  border-radius: 50px;
  background-color: #86b499;
  color: white;
  font-size: 20px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 15px 50px;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.button:hover {
  background-color: #6a8f76;
}

.button2 {
  border-radius: 50px;
  background-color: #798c97;
  color: white;
  font-size: 20px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 15px 50px;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.button2:hover {
  background-color: #0a1113;
}

.small-button {
  border-radius: 50px;
  background-color: #798c97;
  color: white;
  font-size: 12px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 15px 50px;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.small-button:hover {
  background-color: #0a1113;
}

/* Image Hover */

.fade-hover {
  opacity: 0.25; /* Set initial opacity to 10% */
  transition: opacity 0.3s ease; /* Add transition effect for smooth opacity change */
}

.fade-hover:hover {
  opacity: 1; /* Set opacity to 100% on hover */
}

/* Underline Hyperlink Gray */

.a-hyperlink {
  color: darkgray;
  text-decoration: underline;
  font-size: 23px;
  transition: color 0.3s ease;
}

.a-hyperlink:hover {
  color: #0a1113;
  text-decoration: underline;
  font-size: 23px;
}
.a-nav {
  color: gray;
  font-size: 23px;
  margin: 10px;
  transition: color 0.3s ease;
}
.a-nav:hover {
  color: black;
  text-decoration: underline;
  font-size: 23px;
}

#iparallax {
  position: relative;
  transition: transform 0.2s ease-out;
}
